import React from "react";
import { useEffect } from "react";
import { getEmailList } from "../services/EmailList";
import EmailCard from "./EmailCard";
import "../styles/emailListStyles.css";
import { useState } from "react";
import EmailBody from "./EmailBody";

function EmailList() {
  const [mailData, setMailData] = useState([]);
  const [bodyVars, setBodyVars] = useState({
    id: null,
    subject: null,
    name: null,
  });

  useEffect(() => {
    const fetchAllEmails = async () => {
      const data = await getEmailList();
      console.log(data);
      setMailData(data);
    };

    fetchAllEmails();
  }, []);

  console.log(mailId);

  return (
    <section className="section__emails">
      <div className="emails-list">
        {mailData.map((item, index) => {
          return <EmailCard item={item} key={index} setMailId={setMailId} />;
        })}
      </div>
      {/* only show body if a mail is clicked */}
      {mailId && (
        <div className="email-body">
          <EmailBody mailId={mailId} />
        </div>
      )}
    </section>
  );
}

export default EmailList;
